import React, { useState } from "react";
import logo from "../assets/djezzy.png";
import Sidebar from "./Sidebar";
import NavBar from "./NavBar";
import {useStateContext} from "../contexts/ContextProvider";

function Register() {
  const [registerFormData, setRegisterFormData] = useState({
    username: "",
    firstName: "",
    lastName: "",
    phoneNumber: "",
    dateOfBirth: "",
    password: "",
    passwordConfirmation: "",
  });

  const handleChange = (event) => {
    setRegisterFormData({
      ...registerFormData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if(registerFormData.username.length<4)
      setIsvalidUsername(false)
    else {
      setIsvalidUsername(true)
    }

    if(registerFormData.firstName.length<3)
      setIsvalidFirstname(false)
    else {
      setIsvalidFirstname(true)
    }

    if(registerFormData.lastName.length<3)
      setisValidLastname(false)
    else {
      setisValidLastname(true)
    }

    if(registerFormData.phoneNumber.length<10)
      setisValidnumber(false)
    else {
      setisValidnumber(true)
    }

    if(registerFormData.password.length<10)
      setisValidpassword(false)
    else {
      setisValidpassword(true)
    }

    if(registerFormData.password != registerFormData.passwordConfirmation )
      setispasswordMatch(false)
    else {
      setispasswordMatch(true)
    }

  };

  const  {activeMenu} = useStateContext()


  const [isvalidUsername , setIsvalidUsername] = useState(true)
  const [isvalidFirstname , setIsvalidFirstname] = useState(true)
  const [isvalidLastname , setisValidLastname] = useState(true)
  const [isvalidnumber , setisValidnumber] = useState(true)
  const [isvalidpassword , setisValidpassword] = useState(true)
  const [passwordMatch , setispasswordMatch] = useState(true)


  return (
      <div>

    <div className="bg-gray-200 min-h-screen">
      <div className="p-4 mx-auto max-w-lg pt-12">
        <form
          onSubmit={handleSubmit}
          className="bg-white p-6 rounded-md shadow-md">
          <img
            className=" object-scale-down h-20 w-20"
            src={logo}
            alt="djeezy logo"/>
          <div class="font-medium p-4 self-center text-center text-2xl uppercase text-gray-800">
            Créer un compte
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="userName">
              Nom d'utilisateur
            </label>
            <input
              className={ isvalidUsername ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                  : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

              }
              type="text"
              name="username"
              id="userName"
              placeholder="Nom d'utilisateur"
              value={registerFormData.userName}
              onChange={handleChange}
              required/>
            {!isvalidUsername && <p className="text-red-600 ml-3 text-[14px]">Le nom d'utilisateur doit contenir au moins 4 caractères.</p>}
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="firstName">
              Prénom
            </label>
            <input
                className={ isvalidFirstname ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                    : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

                }
              type="text"
              name="firstName"
              id="firstName"
              placeholder="Prénom"
              value={registerFormData.firstName}
              onChange={handleChange}
              required/>
            {!isvalidFirstname && <p className="text-red-600 ml-3 text-[14px]">Le Prenom doit contenir au moins  3 caractères.</p>}
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="lastName">
              Nom
            </label>
            <input
                className={ isvalidLastname ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                    : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

                }
              type="text"
              name="lastName"
              id="lastName"
              placeholder="Nom"
              value={registerFormData.lastName}
              onChange={handleChange}
              required/>
            {!isvalidLastname && <p className="text-red-600 ml-3 text-[14px]">Le Nom doit contenir au moins  3 caractères.</p>}
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="phoneNumber">
              Numéro de téléphone
            </label>
            <input
                className={ isvalidnumber ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                    : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

                }
              type="number"
              name="phoneNumber"
              id="phoneNumber"
              placeholder="XX XX XX XX XX"
              value={registerFormData.phoneNumber}
              onChange={handleChange}
              required/>
            {!isvalidnumber && <p className="text-red-600 ml-3 text-[14px]">Le Numéro de telephone doit contenir 10 chiffres.</p>}
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="dateOfBirth">
              Année de naissance
            </label>
            <input
              className="border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400"
              type="date"
              name="dateOfBirth"
              id="dateOfBirth"
              value={registerFormData.dateOfBirth}
              onChange={handleChange}/>
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="password">
              Mot de passe
            </label>

            <input
                className={ isvalidpassword ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                    : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

                }
              type="password"
              name="password"
              id="password"
              placeholder="Entrez le mot de passe"
              value={registerFormData.password}
              onChange={handleChange}
              required/>
            {!isvalidpassword && <p className="text-red-600 ml-3 text-[14px]">Le mot de passe doit contenir au moins 4 caractères.</p>}
          </div>
          <div className="mb-4">
            <label
              className="block font-bold mb-2 text-gray-700"
              htmlFor="passwordConfirmation">
              Confirmez le mot de passe
            </label>
            <input
                className={ passwordMatch ? "border border-gray-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "
                    : "border border-red-400 p-2 w-full text-base placeholder-gray-500 pr-4 rounded-lg py-2 focus:outline-none focus:border-red-400 "

                }
              type="password"
              name="passwordConfirmation"
              id="passwordConfirmation"
              placeholder="Confirmez le mot de passe"
              value={registerFormData.passwordConfirmation}
              onChange={handleChange}
              required/>
            {!passwordMatch&& <p className="text-red-600 ml-3 text-[14px]">Les mots de passe ne correspondent pas.</p>}
          </div>
          <button
            className="flex items-center justify-center focus:outline-none border-4 border-red-600 rounded-full text-white text-base bg-red-600 hover:bg-red-700 py-2 w-full transition duration-150 ease-in"
            type="submit">
            Créer
          </button>
        </form>
      </div>
    </div>
      </div>
  );
}

export default Register;
