import React, {useEffect, useState} from "react";
import { Table, Tag, Space } from 'antd';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import {getAllUsers} from "../API/UserApiService";

const UserTable = () => {
    const [users, setUsers] = useState([]);


    useEffect(
        ()=> refreshUsers()
    )
   function refreshUsers(){
       getAllUsers()
           .then(response => console.log(response))
           .catch(error => console.log(error))
   }

    const handleDeleteUser = (id) => {

    }

    const handleUpdateUser = (id) => {
        // Add code to handle updating user
    }

    const columns = [
        {
            title: "Nom d'utilisateur",
            dataIndex: 'username',
            key: 'username',
        },
        {
            title: 'Nom',
            dataIndex: 'nom',
            key: 'name',
        },

        {
            title: 'Prenom',
            dataIndex: 'prenom',
            key: 'prenom',
        },
        {
            title: 'Numero de telephone',
            dataIndex: 'number',
            key: 'number',
        },

        {
            title: 'Année de naissance',
            dataIndex: 'birthDate',
            key: 'birthDate',
        },

        {
            title: 'Mot de passe',
            dataIndex: 'password',
            key: 'password',
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <a onClick={() => handleUpdateUser(record.id)}><EditOutlined /></a>
                    <a onClick={() => handleDeleteUser(record.id)}><DeleteOutlined /></a>
                </Space>
            ),
        },
    ];

    return (
        <Table columns={columns} dataSource={users} />
    );
}

export default UserTable;
