import React from 'react';
import {Link , NavLink} from "react-router-dom";
import {SiShopware} from "react-icons/si";
import {MdOutlineCancel} from "react-icons/md";
import  {TooltipComponent} from "@syncfusion/ej2-react-popups";
import {links} from "../data/dummy";
import logo from "../assets/djezzy.png";

function Sidebar() {
    const activeMenu = true
    return (
        <div className='ml-3 h-screen md:overflow-hidden overflow-auto md:hover:overflow-auto pb-10'>
            {activeMenu && (
                <>
                    <div className=" flex justify-between items-center">
                        <Link to="/" onClick={()=>{}} className="items-center gap-3 ml-3 mt-4 flex text-xl font-bold tracking-tight dark:text-white text-slate-900" >
                            <img
                                className=" h-12 w-12 mr-4 ml-2 "
                                src={logo}
                                alt="djeezy logo"
                            />
                            <span>Djezzy</span>
                        </Link>
                        <TooltipComponent content="Menu"  ></TooltipComponent>
                    </div>
                </>
            )

            }
        </div>
    );
}

export default Sidebar;
