import React from 'react';

function Hero() {

    function GotoLogin(){
        window.location.href ='login'
    }
    return (
        <div className="text-black w-full h-screen bg-zinc-200 flex flex-col justify-between">
        <div className='grid md:grid-cols-2 max-w[1240px] m-auto'>
          <div className='flex flex-col justify-center md:items-start'>
              <h1 className='font-bold'>BIENVENUE</h1>
              <p>Veuillez vous connecter pour commencer </p>
              <button className='px-8 py-3'>SE CONNECTER</button>
          </div>
        </div>
        </div>
    );
}

export default Hero;
